    <body class="font-sans antialiased dark:bg-black dark:text-white/50">
      <div class="bg-white">
        <header class="absolute inset-x-0 top-0 z-50">
          <nav class="flex items-center justify-between p-6 lg:px-8" aria-label="Global">
            <div class="flex lg:flex-1">
              <img class="h-11 w-auto" src="/img/logo/logo.png" alt="SkincarePro Logo">
              <p class="h-11 mt-3 text-gray-900">SkincarePro</p>
            </div>
          
            <div class="lg:flex lg:gap-x-12 lg:justify-end">
                <a href="{{ route('aboutus') }}" class="text-sm font-semibold text-gray-900">About Us</a>
                <a href="{{ route('register') }}" class="text-sm font-semibold text-gray-900">Register</a>
                <a href="{{ route('login') }}" class="text-sm font-semibold text-gray-900">Log in</a>
            </div>
        </nav>
          <!-- Mobile menu, show/hide based on menu open state. -->
          <div class="lg:hidden" role="dialog" aria-modal="true">
            <!-- Background backdrop, show/hide based on slide-over state. -->
            <div class="fixed inset-0 z-50"></div>
            <div class="fixed inset-y-0 right-0 z-50 w-full overflow-y-auto bg-white px-6 py-6 sm:max-w-sm sm:ring-1 sm:ring-gray-900/10">
              <div class="flex items-center justify-between">
                <img class="h-11 w-auto" src="/img/logo/logo.png" alt="SkincarePro Logo">
                <p class="h-11 mt-3 text-gray-900">SkincarePro</p>
                <button type="button" class="-m-2.5 rounded-md p-2.5 text-gray-700">
                  <span class="sr-only">Close menu</span>
                  <svg class="size-6" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" aria-hidden="true" data-slot="icon">
                    <path stroke-linecap="round" stroke-linejoin="round" d="M6 18 18 6M6 6l12 12" />
                  </svg>
                </button>
              </div>
              <div class="mt-6 flow-root">
                <div class="-my-6 divide-y divide-gray-500/10">
                  <div class="space-y-2 py-6">
                    <a href="{{ route('aboutus') }}" class="-mx-3 block rounded-lg px-3 py-2 text-base/7 font-semibold text-gray-900 hover:bg-gray-50">About Us</a>
                    <a href="{{ route('register') }}" class="-mx-3 block rounded-lg px-3 py-2 text-base/7 font-semibold text-gray-900 hover:bg-gray-50">Register</a>
                  </div>
                  <div class="py-6">
                    <a href="{{ route('login') }}" class="-mx-3 block rounded-lg px-3 py-2.5 text-base/7 font-semibold text-gray-900 hover:bg-gray-50">Log in</a>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </header>
      
        <div class="relative isolate px-6 pt-14 lg:px-8">
          <div class="absolute inset-x-0 -top-40 -z-10 transform-gpu overflow-hidden blur-3xl sm:-top-80" aria-hidden="true">
            <div class="relative left-[calc(50%-11rem)] aspect-[1155/678] w-[36.125rem] -translate-x-1/2 rotate-[30deg] bg-gradient-to-tr from-[#ff80b5] to-[#9089fc] opacity-30 sm:left-[calc(50%-30rem)] sm:w-[72.1875rem]" style="clip-path: polygon(74.1% 44.1%, 100% 61.6%, 97.5% 26.9%, 85.5% 0.1%, 80.7% 2%, 72.5% 32.5%, 60.2% 62.4%, 52.4% 68.1%, 47.5% 58.3%, 45.2% 34.5%, 27.5% 76.7%, 0.1% 64.9%, 17.9% 100%, 27.6% 76.8%, 76.1% 97.7%, 74.1% 44.1%)"></div>
          </div>
          <div class="mx-auto max-w-2xl py-10 sm:py-20 lg:py-24">
            <div class="text-center">
              <h1 class="text-balance text-5xl font-semibold tracking-tight text-gray-900 sm:text-7xl">WELCOME TO SKINCAREPRO</h1>
              <p class="mt-8 text-pretty text-lg font-medium text-gray-500 sm:text-xl/8">Personalized skincare solutions and your favorite products for every skintype and concerns in one place.</p>
              <div class="mt-10 flex items-center justify-center gap-x-6">
                <a href="{{ route('register') }}" class="rounded-md bg-pink-400 px-3.5 py-2.5 text-sm font-semibold text-white shadow-sm hover:bg-pink-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600">Get started</a>
                <a href="{{ route('register') }}" class="text-sm/6 font-semibold text-gray-900">Learn more <span aria-hidden="true">→</span></a>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div class="relative isolate px-6 pt-14 pb-14 lg:px-8 bg-pink-300">
        <div class="text-center">
            <h1 class="text-5xl font-semibold tracking-tight text-white sm:text-7xl">Choose Your Treatment</h1>
        </div>
    
        <div class="mt-10 flex justify-center gap-10 ">
          <div class="flex flex-col items-start ">
              <button class="treatment-item" data-image="/img/skin_concerns/acne.jpg">Acne</button>
              <button class="treatment-item" data-image="/img/skin_concerns/acne_scars.jpg">Acne Scars & Pigmentation</button>
              <button class="treatment-item" data-image="/img/skin_concerns/ageing.jpg">Ageing</button>
              <button class="treatment-item" data-image="/img/skin_concerns/melasma.jpg">Melasma</button>
              <button class="treatment-item" data-image="/img/skin_concerns/rosacea.jpg">Rosacea</button>
              <button class="treatment-item" data-image="/img/skin_concerns/skin_glow.jpg">Skin Glow</button>
          </div>
      
          <div class="image-container">
              <img id="treatment-image" src="" alt="Treatment Image" class="hidden w-64 h-64 object-cover rounded-lg" />
          </div>
      </div>
        <!-- Footer -->
      <footer class=" text-gray-600 py-6 my-3">
        <div class="container mx-auto px-6 text-center">
          <p>© 2024 SkincarePro. All rights reserved.</p>
        </div>
      </footer>
      <style>
          .treatment-item {
              font-size: 1.25rem;
              color: white;
              background: none; /* Remove default button background */
              border: none; /* Remove default button border */
              text-decoration: none; /* No underline */
              margin-bottom: 10px;
              transition: color 0.3s;
              cursor: pointer; /* Change cursor to pointer */
          }
      
          .treatment-item:hover {
              color: #112979;
          }
      
          .image-container {
              display: flex;
              align-items: center;
              justify-content: center;
              margin-left: 20px;
          }
      
          .hidden {
              display: none;
          }
      </style>
      
      <script>
          document.querySelectorAll('.treatment-item').forEach(item => {
              item.addEventListener('click', function (event) {
                  event.preventDefault(); // Prevent default button behavior
                  const imageSrc = this.getAttribute('data-image');
                  console.log("Image Source:", imageSrc); // Check if the correct path is retrieved
                  const imageElement = document.getElementById('treatment-image');
                  if (imageElement) {
                      console.log("Image Element Found");
                      imageElement.src = imageSrc;
                      imageElement.classList.remove('hidden');
                  } else {
                      console.error("Image Element Not Found");
                  }
              });
          });
      </script>
      
    </body>
</html>
